import { Avatar } from '@material-ui/core';
import React from 'react';
import './MessageSender.scss'
import { Search , Home, Flag , SubscriptionsOutlined , StorefrontOutlined , SupervisedUserCircle , Add , Forum , NotificationsActive , ExpandMore, Videocam} from '@material-ui/icons';

 export default function  MessageSender() {

    const handleSubmit = e => {
        e.preventDefault();
    }
    return (
        <div className = "message__sender">
            <div className="message__sender__top">
                <Avatar src = ""/>
                <form>
                    <input className = "" placeholder = "What's in your mind " />   
                    <input placeholder = "image URL (optional )" />
                    <input type="submit" value="Hidden submit" onClick = {handleSubmit} />
                </form>
            </div>
            <div className="message__sender__bottom">
                <div className="message__sender__bottom__option">
                    <Videocam style = {{color :"red"}} />
                    <h3>Live Video</h3>
                </div>
                <div className="message__sender__bottom__option">
                    <Videocam style = {{color :"red"}} />
                    <h3>Live Video</h3>
                </div>
            </div>
        </div>
    );
}
